static void DeInitKeventIoServiceListenerLocked(struct HdfSysEventNotifier * notifier)
{
    if (notifier->keventIoService == NULL) {
        return;
    }

    (void)HdfDeviceUnregisterEventListener(notifier->keventIoService, &notifier->ioServiceListener);
    HdfIoServiceRecycle(notifier->keventIoService);
    notifier->keventIoService = NULL;
}

rust_code:
fn DeInitKeventIoServiceListenerLocked(notifier: *mut HdfSysEventNotifier) {
    unsafe {
        if notifier.is_null() || (*notifier).keventIoService.is_null() {
            return;
        }
        let _ = HdfDeviceUnregisterEventListener((*notifier).keventIoService, core::ptr::addr_of_mut!((*notifier).ioServiceListener));
        HdfIoServiceRecycle((*notifier).keventIoService);
        (*notifier).keventIoService = core::ptr::null_mut();
    }
}
