pub extern \"C\" fn HdfSysEventNotifyUnregister(notifierNode: *mut HdfSysEventNotifyNode) {
    unsafe fn dlist_remove(node: *mut DListHead) {
        (*(*node).prev).next = (*node).next;
        (*(*node).next).prev = (*node).prev;
        (*node).next = node;
        (*node).prev = node;
    }
    unsafe fn dlist_is_empty(head: *mut DListHead) -> bool {
        (*head).next == head
    }

    if notifierNode.is_null() {
        return;
    }
    let notifier = HdfSysEventNotifierGetInstance();
    if notifier.is_null() {
        return;
    }
    unsafe {
        let _ = OsalMutexLock(core::ptr::addr_of_mut!((*notifier).mutex));
        dlist_remove(core::ptr::addr_of_mut!((*notifierNode).listNode));
        if dlist_is_empty(core::ptr::addr_of_mut!((*notifier).notifyNodeList)) {
            DeInitKeventIoServiceListenerLocked(notifier);
        }
        let _ = OsalMutexUnlock(core::ptr::addr_of_mut!((*notifier).mutex));
    }
}
