static int GetProductName(char * name, int maxLen)
{
    return  strcpy_s(name, maxLen, "default");
}

rust_code:
fn GetProductName(name: *mut ::core::ffi::c_char, maxLen: ::core::ffi::c_int) -> ::core::ffi::c_int {
    unsafe {
        // securec: errno_t == 0 on success
        strcpy_s(
            name,
            maxLen as crate::types::size_t,
            b"default\0".as_ptr() as *const ::core::ffi::c_char,
        )
    }
}
