pub extern \"C\" fn HdfDeviceInfoConstruct(deviceInfo: *mut HdfDeviceInfo) {
    if deviceInfo.is_null() {
        return;
    }
    unsafe {
        (*deviceInfo).isDynamic = false;
        (*deviceInfo).status = HDF_SERVICE_UNUSABLE as u16;
        (*deviceInfo).deviceType = HDF_DEV_LOCAL_SERVICE as u16;
        (*deviceInfo).deviceId = 0;
        (*deviceInfo).policy = SERVICE_POLICY_INVALID as u16;
        (*deviceInfo).priority = 0;
        (*deviceInfo).preload = DEVICE_PRELOAD_ENABLE as u16;
        (*deviceInfo).permission = 0;
        (*deviceInfo).svcName = core::ptr::null();
        (*deviceInfo).moduleName = core::ptr::null();
        (*deviceInfo).deviceMatchAttr = core::ptr::null();
        (*deviceInfo).deviceName = core::ptr::null();
    }
}
