c_code:
void HdfDeviceInfoFreeInstance(struct HdfDeviceInfo * deviceInfo)
{
    if (deviceInfo != NULL) {
        OsalMemFree(deviceInfo);
    }
}

rust_code:
pub extern \"C\" fn HdfDeviceInfoFreeInstance(deviceInfo: *mut HdfDeviceInfo) {
    if !deviceInfo.is_null() {
        unsafe { OsalMemFree(deviceInfo as *mut core::ffi::c_void) };
    }
}
