struct HdfObject * HdfObjectManagerGetObject(int objectId)
{
    struct HdfObject *object = NULL;
    const struct HdfObjectCreator *targetCreator = HdfObjectManagerGetCreators(objectId);
    if ((targetCreator != NULL) && (targetCreator->Create != NULL)) {
        object = targetCreator->Create();
        if (object != NULL) {
            object->objectId = objectId;
        }
    }
    return object;
}

rust_code:
pub extern \"C\" fn HdfObjectManagerGetObject(objectId: core::ffi::c_int) -> *mut HdfObject {
    unsafe {
        let creator = HdfObjectManagerGetCreators(objectId);
        if creator.is_null() || (*creator).Create.is_none() {
            return core::ptr::null_mut();
        }
        let obj = (*creator).Create.unwrap()();
        if !obj.is_null() {
            (*obj).objectId = objectId;
        }
        obj
    }
}
