void HdfObjectManagerFreeObject(struct HdfObject * object)
{
    const struct HdfObjectCreator *targetCreator = NULL;
    if (object == NULL) {
        return;
    }
    targetCreator = HdfObjectManagerGetCreators(object->objectId);
    if ((targetCreator == NULL) || (targetCreator->Release == NULL)) {
        return;
    }
    targetCreator->Release(object);
}

rust_code:
pub extern \"C\" fn HdfObjectManagerFreeObject(object: *mut HdfObject) {
    if object.is_null() {
        return;
    }
    unsafe {
        let creator = HdfObjectManagerGetCreators((*object).objectId);
        if creator.is_null() || (*creator).Release.is_none() {
            return;
        }
        (*creator).Release.unwrap()(object);
    }
}
