void DevSvcRecordFreeInstance(struct DevSvcRecord * inst)
{
    if (inst != NULL) {
        OsalMemFree((char *)inst->servName);
        OsalMemFree((char *)inst->servInfo);
        OsalMemFree((char *)inst->interfaceDesc);
        OsalMemFree(inst);
    }
}

rust_code:
pub extern \"C\" fn DevSvcRecordFreeInstance(inst: *mut DevSvcRecord) {
    if inst.is_null() {
        return;
    }
    unsafe {
        OsalMemFree((*inst).servName as *mut core::ffi::c_void);
        OsalMemFree((*inst).servInfo as *mut core::ffi::c_void);
        OsalMemFree((*inst).interfaceDesc as *mut core::ffi::c_void);
        OsalMemFree(inst as *mut core::ffi::c_void);
    }
}
