void SvcMgrIoserviceRelease(struct ISvcMgrIoservice * svcmgr)
{
    if (svcmgr == NULL) {
        return;
    }
    struct SvcMgrIoservice *svcmgrInst = CONTAINER_OF(svcmgr, struct SvcMgrIoservice, svcmgr);
    HdfIoServiceRecycle(svcmgrInst->iosvc);
    OsalMemFree(svcmgrInst);
}

rust_code:
pub extern \"C\" fn SvcMgrIoserviceRelease(svcmgr: *mut ISvcMgrIoservice) {
    if svcmgr.is_null() {
        return;
    }
    unsafe {
        let svcmgrInst = (svcmgr as *mut u8).offset(-(core::mem::offset_of!(SvcMgrIoservice, svcmgr) as isize)) as *mut SvcMgrIoservice;
        HdfIoServiceRecycle((*svcmgrInst).iosvc);
        OsalMemFree(svcmgrInst as *mut core::ffi::c_void);
    }
}
